// Initialize two pointers to the start and end of the array 
int left = 0; 
int right = array.length - 1; 

// Loop through the array until left and right pointers cross 
while (left < right) { 

// Move left pointer until negative integer is found 
while (array[left] >= 0 && left < right) { 
    left++; 
} 

// Move right pointer until negative integer is found 
while (array[right] < 0 && left < right) { 
    right--; 
} 

// Swap positive and negative integers 
if (left < right) { 
    int temp = array[left]; 
    array[left] = array[right]; 
    array[right] = temp; 
} 

}
// Return rearranged array 
return array;

#include <iostream> 
#include <algorithm>  
#include <map> 
using namespace std; 

// Returns frequency of every element in arr[] 
map<int, int> countFrequency(int arr[], int n) 
{ 
	map<int, int> freq; 
	for (int i=0; i<n; i++) 
		freq[arr[i]]++; 

	return freq; 
} 

int main() 
{ 
	int arr[] = {1, 3, 5, 4, 3, 5, 2}; 
	int n = sizeof(arr)/sizeof(arr[0]); 

	// Print frequency 
	map<int, int> freq = countFrequency(arr, n); 
	for (auto itr : freq) 
		cout << itr.first <<  " -> " << itr.second <<endl; 

	return 0; 
}
